mapper.update:
UPDATE m_burn SET
mid = #{mid}, volume_label = #{volume_label}, task_name = #{task_name}, disc_type = #{disc_type}, data_source = #{data_source}, data_type = #{data_type}, burning_machine = #{burning_machine}, burning_state = #{burning_state}, disc_number = #{disc_number}, burn_desc = #{burn_desc}, burn_time = #{burn_time}, update_time = #{update_time}
WHERE mid = #{mid}

mapper.select:
SELECT * FROM m_burn_detail 

	1 = 1


 	and volume_label = #{volLabel} 
 

	and electronic_tag = #{elTag} 
 
 
	and disc_position = #{discPos}

SELECT e.eid, e.volume_label, e.export_state, e.export_path, b.disc_position, b.electronic_tag, b.iso_file FROM export_data_record e left join m_burn_detail b on e.volume_label = b.volume_label

	1 = 1
	
		and e.volume_label = #{volLabel}
	
	
		and e.export_state = #{state}
	
	
		and 1=1
	
	order by eid desc

mapper.update:
UPDATE
export_data_record
SET
eid = #{eid}

	, number_success = #{number_success}


	, export_state = #{export_state}


	, export_desc = #{export_desc}

, update_time = #{update_time}
WHERE
eid = #{eid}

UPDATE
export_data_record
SET

	, number_success = #{number_success}


	, export_state = #{export_state}


	, export_desc = #{export_desc}

, update_time = #{update_time}
WHERE
volume = #{volume_label}
