mapper.select:
SELECT r.work_area, sum(b.d) as d, sum(b.s) as s from ( SELECT volume_label, sum(disc_number) as d, sum(burn_size) as s from m_burn where data_type='R' and burning_state = '8'
and burn_time BETWEEN #{startDate} and #{endDate}
group by volume_label) b left join ( SELECT volume_label, work_area from dq_raw_data group by volume_label, work_area ) r on b.volume_label = r.volume_label group by r.work_area

select * from standing_book 

	1 = 1 
	
		and data_type = #{data_type}
	
	
		and type = #{type}
	
	
		and states = #{states}
	
	
		and work_area like  CONCAT(CONCAT('%', #{work_area}), '%')
	

	
		and construction_unit = #{construction_unit}
	
	
		and construction_year like CONCAT(CONCAT('%', #{construction_year}), '%')   
	
	
		and create_time between #{startDate} and #{endDate}
	
	order by sid desc

select * from standing_book

	 1 = 1
	
		and volume_label = #{volume_label}
	
		
		and type = #{type}
	
	
		and work_area = #{work_area}
	
	
		and construction_year = #{construction_year}
	
	
		and eid = #{eid}

mapper.update:
update standing_book 
set
	volume_label = #{volume_label}
	
		, update_time = #{update_time}
	
	
		, states = #{states}
	
	
		, data_quantity = data_quantity + #{data_quantity}
	
	
		, burn_count = #{burn_count}
	
where
	volume_label = #{volume_label}
	
		and work_area = #{work_area}
	
	
		and type = #{type}
	
		
		and eid = #{eid}
